sys_variable_value.value:
(function execute(inputs, outputs) {
  	var outputsToShow = [];
  	var inputsToShow = [];
	var responseBody = JSON.parse(inputs.response_body);
    var arrOutputs = responseBody.result.outputs;
  	var arrInputs = responseBody.result.inputs;
  	outputs.output = responseBody.result;
  	arrOutputs.map(function(element){
      if(responseBody.result && responseBody.result.action_template === "DATASTREAM") {
      		if(element.name !== "item_count" && element.name !== "page_count" && element.name !== "count" && element.name !== "ds_count" && element.name !== "item" && element.name !== "ds_output_page_variables" && element.name !== "response_headers" && element.name !== "error_message" && element.name !== "ds_parsed_stream" && element.name !== "error_code" && element.name !== "status_code"){
            	outputsToShow = element.children;
            }
      } else {
        if(element.name !== "__action_status__" && element.name !== "__dont_treat_as_error__"){
            	outputsToShow.push({
                  "name": element.name,
                  "label": element.label
                });
        } 
      }
    });
  	arrInputs.map(function(element){
    	inputsToShow.push({
        	"name": element.name,
          	"label": element.label,
          	"type": element.type
        });
    });
  	outputs.action_outputs = outputsToShow;
  	outputs.action_inputs = inputsToShow;
})(inputs, outputs);

